// Try to map from existing DB Articles for Images/Links if missing
const getDetails = (keyword) => {
    let img = imageMap[keyword];
    let url = libMap[keyword];

    // Fuzzy search in DB for link if missing
    if (!url) {
        // Try to match in myvideo_library table
        const row = db.prepare("SELECT url FROM myvideo_library WHERE title LIKE ?").get(`%${keyword}%`);
        if (row) url = row.url;
    }

    // Fallbacks
    if (!img) {
        // Try to find image from articles table
        const row = db.prepare("SELECT image_url FROM articles WHERE title LIKE ? AND image_url IS NOT NULL").get(`%${keyword}%`);
        if (row) img = row.image_url;
    }

    return {
        img: img || '/assets/placeholder.jpg',
        url: url || `https://www.myvideo.net.tw/search/${encodeURIComponent(keyword)}`
    };
};

