const findLink = (name) => {
    const row = db.prepare("SELECT url FROM myvideo_library WHERE title LIKE ? ORDER BY length(title) ASC LIMIT 1").get(`%${name}%`);
    return row ? row.url : `https://www.myvideo.net.tw/search?keyword=${encodeURIComponent(name)}`;
};

const findImg = (name) => {
    const row = db.prepare("SELECT image_url FROM myvideo_library WHERE title LIKE ? AND image_url IS NOT NULL ORDER BY length(title) ASC LIMIT 1").get(`%${name}%`);
    return row ? row.image_url : '/assets/placeholder.jpg';
};

const updateArticle = (id, data, isSeasonal = false) => {
    let markdown = `## **${data.excerpt}**\n\n---\n\n`;
    let coverImg = null;

    data.items.forEach((item, index) => {
        const name = typeof item === 'string' ? item : item.t;
        const desc = typeof item === 'string' ? rawContent[name] : item.desc;
        const img = findImg(name);
        if (!coverImg && img !== '/assets/placeholder.jpg') coverImg = img;
        const link = findLink(name);

        markdown += `### ${index + 1}. ã€Š${name}ã€‹\n\n`;
        markdown += `![${name}](${img})\n\n`;
        markdown += `**å…§å®¹ä»‹ç´¹ï¼š** ${desc}\n\n`;
        markdown += `<p align="center">ğŸ‘‰ <a href="${link}" target="_blank">å‰å¾€ MyVideo ç·šä¸Šè§€çœ‹</a></p>\n\n`;
        markdown += `---\n\n`;
    });

    const finalCover = coverImg || '/assets/placeholder.jpg';
    const stmt = db.prepare('UPDATE articles SET title = ?, content = ?, excerpt = ?, image_url = ?, category = ?, is_pinned = 1 WHERE id = ?');
    stmt.run(data.title, markdown, data.excerpt, finalCover, 'ç·¨è¼¯ç²¾é¸', id);
    console.log(`âœ… Fixed ID ${id}: ${data.title}`);
};

