async function seed() {
    console.log('üöÄ Seeding v9: Content Expansion Marathon (>800 chars)...');
    const db = new Database(dbPath);

    const updateStmt = db.prepare(`
        UPDATE articles 
        SET title = ?, excerpt = ?, content = ?, category = ?, image_url = ?
        WHERE slug = ?
    `);

    let successCount = 0;

    for (const article of articles) {
        // Validation: Check content length roughly
        if (article.content.length < 500) {
            console.warn(`‚ö†Ô∏è Warning: Content for ${article.slug} is too short (${article.content.length} chars).`);
        }

        const result = updateStmt.run(
            article.title,
            article.excerpt,
            article.content,
            article.category,
            article.image,
            article.slug
        );

        if (result.changes > 0) {
            console.log(`‚úÖ Updated: ${article.slug} (Len: ${article.content.length})`);
            successCount++;
        } else {
            console.log(`‚ùå Error: Article not found: ${article.slug}. Run fix-slugs.js first?`);
        }
    }

    console.log(`\\nüéâ Marathon Complete! Updated ${successCount}/${articles.length} articles.`);
}

seed();
